<script>
export default {
    methods: {
        toggleSidebar() {
            let sidebar = document.getElementById('sidebar');
            if( sidebar.classList.contains('sidebar-active')) {
                sidebar.classList.remove('sidebar-active');
                document.body.style.overflow = "auto";
            } else {
                sidebar.classList.add('sidebar-active');
                document.body.style.overflowY = "hidden";
            }
        }
    },
    computed: {
        username() {
            if(this.$store.state.user) {
                return this.$store.state.user.displayName;
            }
        }
    }
}
</script>

<style lang="scss">
@import "../assets/root.scss";

.sidebar {
    position: fixed;
    top: 0;
    left: -100%;
    width: 80%;
    height: 100%;
    max-width: 400px;
    min-width: 250px;
    z-index: 12;
    transition: .2s all;
    background: white;
    color: black;
    
    font-family: 'Red Hat Text', sans-serif;

    .sidebar-top {
        background: $main-blue;
        color: white;
        height: 70px;
        padding: 10px;
        display: flex;
        flex-direction: column;
        justify-content: flex-end;

        h1 {
            font-size: 24px;
            font-style: normal;
            font-weight: bold;
        }

        h2 {
            color: #CBCBCB;
            font-style: normal;
            font-weight: bold;
            font-size: 14px;
        }
    }

    .sidebar-links {
        display: flex;
        flex-direction: column;
        justify-content: flex-start;
        padding: 21px 13px 42px;
        border-bottom: 1px solid #E5E5E5;

        a {
            font-style: normal;
            font-weight: bold;
            font-size: 14px; 
            color: $blue-text;
            margin-bottom: 10px;
            text-decoration: none;

            svg {
                margin-right: 17px;
            }
            
        }
    }

    .premium-section {
        .premium-top {
            display: flex;
            justify-content: space-between;
            padding: 5px 13px;

            h2 {
                font-style: normal;
                font-weight: bold;
                font-size: 14px;

                &.premium-title {
                    color: #E5E5E5;
                }

                &.premium-status {
                    color: $blue-text;
                }
            }
        }
        .premium-links {
            padding: 13px;

            a {
                font-style: normal;
                font-weight: bold;
                font-size: 14px; 
                color: #E5E5E5;;
                margin-bottom: 10px;
                text-decoration: none;

                svg {
                    transform: rotate(15.8deg) translateY(7px);

                }

                .text {
                    margin-left: 13px;
                }
            }
        }
    }
}

.sidebar-active {
    left: 0;

    .sidebar-background {
        display: block;
        position: absolute;
        top: 0;
        left: 100%;
        height: 100vh;
        width: 100vw;
        background: rgba(0,0,0,.6);
        z-index: -1;
    }
}


.sidebar-background {
    display: none;
}

